import React, { useState } from 'react';
import { Form, Input, Button, Alert } from 'antd';
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [errorMessage, setErrorMessage] = useState('');

  const onFinish = async (values) => {
    try {
      // Extract the email from the form values
      const { email } = values;

      // Determine the endpoint URL based on the email domain or any other criteria
      let endpointURL = '';
      if (email.endsWith('@example.com')) {
        // If the email ends with '@example.com', it's an admin user
        endpointURL = 'http://localhost:5000/api/admin/login';
      } else {
        // For other users, use the regular login endpoint
        endpointURL = 'http://localhost:5000/api/login';
      }

      // Send a POST request to the determined endpoint with the form values
      const response = await axios.post(endpointURL, values);

      // Check if the response contains admin data and a token for admin login
      if (email.endsWith('@example.com') && response.data.admin && response.data.token) {
        // Admin login successful
        localStorage.setItem('adminToken', response.data.token);
        onLogin(); // Call the parent component's login handler
      } else {
        // Handle error or login for regular users
        // You can adjust this based on your application's requirements
        if (response.data.success) {
          // Handle successful login for regular users
        } else {
          // Handle error (e.g., invalid credentials)
          setErrorMessage('Invalid email or password');
        }
      }
    } catch (error) {
      // Handle network errors or other exceptions
      console.error('Error:', error);
      setErrorMessage('An error occurred. Please try again later.');
    }
  };



  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };

  const handleCancel = () => {
    console.log('Login cancelled');
  };

  return (
    <div>
      {errorMessage && <Alert message={errorMessage} type="error" showIcon />}
      <Form
        name="basic"
        initialValues={{ remember: true }}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
      >
        <Form.Item
          label="Email"
          name="email"
          rules={[{ required: true, message: 'Please input your email!' }]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="Password"
          name="password"
          rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <Input.Password />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit">
            Log in
          </Button>
          <Button onClick={handleCancel}>Cancel</Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default Login;
